import React from "react";
import { NavLink, useLocation } from "react-router-dom";

const SideBar = () => {
  var currentLocation = useLocation().pathname;

  console.log(currentLocation);
  return (
    <div className="sidebar">
      <div className="profile">
        <img src="./images/company-logo.jpg" alt="profile_picture" />
        <h3>My Company</h3>
      </div>
      <ul>
        <li>
          <NavLink to="/" activeClassName="active">
            <span className="item">Home</span>
          </NavLink>
        </li>
        <li>
          <NavLink to="/page-1" activeClassName="active">
            <span className="item">Page 1</span>
          </NavLink>
        </li>
        <li>
          <NavLink to="/page-2" activeClassName="active">
            <span className="item">Page 2</span>
          </NavLink>
        </li>
      </ul>
    </div>
  );
};

export default SideBar;
